DROP TABLE IF EXISTS `tb_commodity`;
CREATE TABLE `tb_commodity` (
  `id` bigint NOT NULL COMMENT '商品表主键ID',
  `duration` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '显示名称(1个  2个   3个)',
  `price` decimal(10,2) DEFAULT NULL COMMENT '商品价格',
  `commodity_specification` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '商品规格(金币为个  会员为天或月)',
  `give_diamond` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '赠送数量',
  `commodity_to_sort` int DEFAULT NULL COMMENT '商品排序',
  `type` int DEFAULT NULL COMMENT '商品类型(1:钻石  2:VIP)',
  `status` int DEFAULT NULL COMMENT '是否开启(1:是  2:否)',
  `label` int DEFAULT NULL COMMENT '标签开启(0:开启  1:关闭)',
  `brief_introduction` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '商品简介(例如优惠,特惠等字数较少的介绍)',
  `time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='商品表';

