-- ----------------------------
-- Table structure for tb_pay_order
-- ----------------------------
DROP TABLE IF EXISTS `tb_pay_order`;
CREATE TABLE `tb_pay_order` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` bigint DEFAULT NULL COMMENT '用户ID',
  `user_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '充值用户昵称',
  `commodity_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '充值商品名称',
  `commodity_type` int DEFAULT NULL COMMENT '充值商品类型(1:钻石  2:VIP)',
  `commodity_specification` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '商品规格(钻石是数量  VIP是天数)',
  `pey_type` int DEFAULT NULL COMMENT '支付类型(1:支付宝  2:微信)',
  `pey_status` int DEFAULT NULL COMMENT '拉起方式(1:APP  2:H5  3:小程序)',
  `order_code` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '平台订单号',
  `trilateral_code` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '三方支付订单号(查账订单号)',
  `recharge_amount` decimal(10,2) DEFAULT NULL COMMENT '充值金额(元)',
  `order_type` int DEFAULT NULL COMMENT '订单支付状态(0:订单创建  1:支付成功  2:支付失败  3:订单超时)',
  `found_time` datetime DEFAULT NULL COMMENT '订单创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=951 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='充值订单表';

