-- ----------------------------
DROP TABLE IF EXISTS `tb_post`;
CREATE TABLE `tb_post` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '帖子ID',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '帖子文字内容/红包文字/问答的json数据',
  `file_urls` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '文件地址,json格式',
  `user_id` bigint DEFAULT NULL COMMENT '发帖的人的ID',
  `circle_id` bigint DEFAULT NULL COMMENT '圈子ID',
  `status` int DEFAULT NULL COMMENT '状态(0审核中1审核通过1拒绝)',
  `type` int DEFAULT NULL COMMENT '类型(0公开帖子1圈内帖子)',
  `create_time` datetime DEFAULT NULL COMMENT '发帖时间',
  `file_type` int DEFAULT NULL COMMENT '文件类型(0图片1视频)',
  `discuss_id` bigint DEFAULT NULL COMMENT '话题ID',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '标题',
  `image` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '封面图(多张图片就是第一张/视频就是第一帧)',
  `dz_count` int DEFAULT NULL COMMENT '点赞数',
  `read_numer` int DEFAULT NULL COMMENT '阅读数',
  `ip` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '发帖IP',
  `ip_locate` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'IP地区',
  `post_class` int DEFAULT NULL COMMENT '帖子类型(0普通贴1红包贴2投票贴3问答贴)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=531 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='帖子表';

