DROP TABLE IF EXISTS `tb_user`;
CREATE TABLE `tb_user` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `nick_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '用户名',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `cion` decimal(10,2) DEFAULT NULL COMMENT '金币数量',
  `balance` decimal(10,2) DEFAULT '0.00' COMMENT '提现余额',
  `avatar` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '用户头像',
  `pass_word` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '密码',
  `vip_date` datetime DEFAULT NULL COMMENT 'VIP到期时间',
  `wx_openid` varchar(255) DEFAULT NULL COMMENT '微信openid',
  `wx_unionid` varchar(255) DEFAULT NULL COMMENT '微信Unionid',
  `email` varchar(255) DEFAULT NULL COMMENT '邮箱',
  `mobile` varchar(255) DEFAULT NULL COMMENT '手机号',
  `gender` int DEFAULT NULL COMMENT '性别(0男1女2未知)',
  `birth` varchar(255) DEFAULT NULL COMMENT '生日',
  `emotion` varchar(255) DEFAULT NULL COMMENT '婚姻状态',
  `job` varchar(255) DEFAULT NULL COMMENT '职位',
  `city` varchar(255) DEFAULT NULL COMMENT '城市',
  `post_proportion` decimal(10,2) DEFAULT '0.00' COMMENT '圈子内付费贴分成比例',
  `withdrawal_proportion` decimal(10,2) DEFAULT '0.00' COMMENT '提现比例',
  `level` int DEFAULT NULL COMMENT '等级',
  `experience` bigint DEFAULT NULL COMMENT '经验',
  `bg_img` longtext COMMENT '个人中心背景图',
  `constellation` varchar(24) DEFAULT NULL COMMENT '星座',
  `login_ip` varchar(255) DEFAULT NULL COMMENT 'IP',
  `last_login_time` datetime DEFAULT NULL COMMENT '最后活跃时间',
  `signature` varchar(255) DEFAULT NULL COMMENT '个性签名',
  `status` int DEFAULT '0' COMMENT '用户状态(0正常1封禁)',
  `lock_reason` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '封禁原因',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `username` (`nick_name`)
) ENGINE=InnoDB AUTO_INCREMENT=10000151 DEFAULT CHARSET=utf8mb3 COMMENT='用户';

