  `type` int DEFAULT NULL COMMENT '回复状态(0:未回复  1:已回复)',
  `status` int DEFAULT NULL COMMENT '是否解决(0:为解决 1:已解决 2:未知)',
  `feedback_time` datetime DEFAULT NULL COMMENT '反馈时间',
  `reply_time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '回复时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1726529728844234941 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

