-- ----------------------------
-- Table structure for tb_withdrawal
-- ----------------------------
DROP TABLE IF EXISTS `tb_withdrawal`;
CREATE TABLE `tb_withdrawal` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `withdrawal_code` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '提现单号',
  `user_id` bigint DEFAULT NULL COMMENT '用户ID',
  `bank_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '银行名称',
  `bank_no` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '银行卡卡号',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '持卡人姓名',
  `idno` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '身份证号',
  `withdrawal_species` decimal(10,2) DEFAULT NULL COMMENT '提现金币数量',
  `withdrawal_money` decimal(10,2) DEFAULT NULL COMMENT '提现金额(金币换算的人民币元)',
  `actual_account` decimal(10,2) DEFAULT NULL COMMENT '实际到账金额(元)',
  `service_charge` decimal(10,2) DEFAULT NULL COMMENT '提现手续费(元)',
  `service_charge_scale` decimal(10,2) DEFAULT NULL COMMENT '提现手续费比例(%)',
  `front_species` decimal(10,2) DEFAULT NULL COMMENT '提现前的金币余额',
  `behind_species` decimal(10,2) DEFAULT NULL COMMENT '提现后的金币余额',
  `status` int DEFAULT NULL COMMENT '审核状态(0:待审核  1:审核通过  2:审核拒绝)',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '审核内容/代付回调内容(拒绝填写拒绝理由  成功不用填写)',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='用户提现代付表';

